import 'package:flutter/material.dart';import 'package:get/get.dart';import 'package:sound_app/utilities/colors.dart';import 'package:sound_app/views/auth/login_screen.dart';void main() {  runApp(const MyApp());}class MyApp extends StatelessWidget {  const MyApp({super.key});  @override  Widget build(BuildContext context) {    return GetMaterialApp(      title: 'Flutter Demo',      debugShowCheckedModeBanner: false,      theme: ThemeData(        colorScheme: ColorScheme.fromSeed(seedColor: AppColors.primaryColor),        useMaterial3: true,      ),      home: const LoginScreen(),    );  }}